@timer_decorator
def get_reference(use_for_check_items: dict, use_for_check_items_opinion_similarity_dic: dict,
                  use_for_check_items_content_dic: dict, query_list: list,
                  min_similarity_with_query: float = 0.5, top_n_indices: int = 32, mode='DocAnswerAINews'):
    """
    在给定的数据中，取出和指定问题最相关的top_n个

    """
    # 遍历嵌套字典的值
    all_values = []
    all_values_key = []
    for key1, inner_dict in use_for_check_items.items():
        all_values.append(inner_dict['description'])
        all_values_key.append(key1)

    all_short_sentence_lines = []
    ref_content_similarity = []
    ref_content_chunk_num = []
    for ref_key, ref_info in use_for_check_items.items():
        try:
            ref_content_similarity.append(ref_info['other_info']['score'])
            all_short_sentence_lines.append(ref_info['description'])
            ref_content_chunk_num.append(
                ref_info['other_info'].get('doc_id', '') + '<<<sorted_flag>>>' + str(ref_info['other_info'].get('chunk_num', '')))
        except:
            log.debug("Something Wrong when find best N reference !\nref_key: {}\nref_info: {}".format(
                ref_key, ref_info))

    # if 'PDFDocAnswer' in mode:
    #     def sort_key(file_name):
    #         # 使用正则提取最后的数字部分
    #         # 提取文件路径部分和数字部分
    #         doc_name = file_name.rsplit('<<<sorted_flag>>>', 1)[0]  # 文件路径部分
    #         match = re.search(r'<<<sorted_flag>>>(\d+)$', file_name)
    #         chunk_num = int(match.group(1)) if match else 0  # 数字部分
    #         return (doc_name, chunk_num)
    #
    #     sorted_indices = [i for i, _ in sorted(enumerate(ref_content_chunk_num), key=lambda x: sort_key(x[1]))]
    #     top_N_indices = sorted_indices[:top_n_indices]
    #     top_N_sim = [ref_content_similarity[i] for i in top_N_indices]
    #     need_check_index_all = top_N_indices[:]
    # else:
    top_N_indices = np.argsort(ref_content_similarity)[-top_n_indices:]
    top_N_sim = [ref_content_similarity[i] for i in top_N_indices]
    # 因为argsort是按升序排列的，所以需要反转索引以得到最大的N个元素
    need_check_index_all = top_N_indices[::-1]
    top_N_sim = top_N_sim[::-1]

    check_items_query = dict()
    ref_str_item = ""
    ref_str_item_info = []
    for i, sim in zip(need_check_index_all, top_N_sim):
        ref_content = all_short_sentence_lines[i]
        if ref_content in all_values:
            ref_content_key = "%s" % use_for_check_items_content_dic[ref_content]
            try:
                check_items_query[ref_content_key] = use_for_check_items[ref_content_key]
            except:
                log.error("error!")
            one_item = "%s: %s \n" % (ref_content_key, ref_content)
            ref_str_item += one_item

            ref_dict_item = copy.deepcopy(use_for_check_items[ref_content_key])
            ref_dict_item['simi2query'] = sim
            ref_dict_item['key'] = ref_content_key
            ref_str_item_info.append(ref_dict_item)

    return check_items_query, ref_str_item, ref_str_item_info


def get_reference_balanced(
        use_for_check_items_origin: dict,
        use_for_check_items_opinion_similarity_dic_origin: dict,
        use_for_check_items_content_dic_origin: dict,
        query_list: list,
        min_similarity_with_query: float = 0.0001,
        top_n_indices: int = 32):
    """
    按照每个查询均匀分配满足条件的 use_for_check_items，同时输出和特定查询相匹配的格式化结果。
    """
    use_for_check_items, use_for_check_items_opinion_similarity_dic, use_for_check_items_content_dic = (
        copy.deepcopy(use_for_check_items_origin), copy.deepcopy(use_for_check_items_opinion_similarity_dic_origin),
        copy.deepcopy(use_for_check_items_content_dic_origin)
    )
    used_items = set()

    # 初始化数据结构
    all_results = dict()

    for item_key_i, item_value_i in use_for_check_items_origin.items():
        query_keyword_i = item_value_i['other_info'].get('query_keyword')
        if query_keyword_i in query_list:
            if query_keyword_i in all_results:
                query_this_item_list = all_results[query_keyword_i]
            else:
                query_this_item_list = [dict(), '', list()]
                all_results[query_keyword_i] = query_this_item_list

            item_info = copy.deepcopy(item_value_i)
            if query_keyword_i in use_for_check_items_opinion_similarity_dic_origin[item_value_i['description']]:
                simi2query_i = use_for_check_items_opinion_similarity_dic_origin[item_value_i['description']][query_keyword_i]
                if simi2query_i > min_similarity_with_query:
                    query_this_item_list[0].update({item_key_i: item_value_i})
                    query_this_item_list[1] += f"{item_key_i}: {item_value_i['description']}\n"
                    item_info['simi2query'] = simi2query_i
                    item_info['key'] = item_key_i
                    query_this_item_list[2].append(item_info)
                    # FIXME: maybe bug here
                    # use_for_check_items.pop(item_key_i)
                    # use_for_check_items_opinion_similarity_dic.pop(item_value_i['description'])
                    # use_for_check_items_content_dic.pop(item_value_i['description'])
            else:
                log.debug("\n query_keyword_i {} not in simi_dic: {}".format(
                    query_keyword_i, use_for_check_items_opinion_similarity_dic_origin[item_value_i['description']]))

    all_results_new = copy.deepcopy(all_results)
    # 按查询分组收集候选项目
    query_candidates = defaultdict(list)
    for ref_content, simi_list in use_for_check_items_opinion_similarity_dic.items():
        for query, similarity in simi_list.items():
            if query in query_list and similarity > min_similarity_with_query:
                ref_key = use_for_check_items_content_dic[ref_content]
                query_candidates[query].append((similarity, ref_key))

    # 优先分配候选项少的查询
    for query, candidates in sorted(query_candidates.items(), key=lambda x: len(x[1])):
        # 排序候选项目
        candidates.sort(reverse=True, key=lambda x: x[0])

        # 初始化当前查询的返回结构
        check_items_query = {}
        ref_str_item = ""
        ref_str_item_info = []

        # 尝试分配第一个项目
        if candidates:
            similarity, ref_key = candidates.pop(0)
            # if ref_key not in used_items:
            item_info = copy.deepcopy(use_for_check_items[ref_key])
            item_info['simi2query'] = similarity
            item_info['key'] = ref_key

            # 填充查询项目
            check_items_query[ref_key] = use_for_check_items[ref_key]
            ref_str_item += f"{ref_key}: {use_for_check_items[ref_key]['description']}\n"
            ref_str_item_info.append(item_info)
            used_items.add(ref_key)

        # 分配剩余项目
        for similarity, ref_key in sorted(candidates, reverse=True, key=lambda x: x[0]):
            if len(ref_str_item_info) < top_n_indices:
                # and ref_key not in used_items
                item_info = copy.deepcopy(use_for_check_items[ref_key])
                item_info['simi2query'] = similarity
                item_info['key'] = ref_key

                # 填充查询项目
                check_items_query[ref_key] = use_for_check_items[ref_key]
                ref_str_item += f"{ref_key}: {use_for_check_items[ref_key]['description']}\n"
                ref_str_item_info.append(item_info)
                used_items.add(ref_key)

        if query in all_results_new:
            query_this_item_list_new = all_results_new[query]
        else:
            query_this_item_list_new = [dict(), '', list()]
            all_results_new[query] = query_this_item_list_new

        # 组装当前查询的结果
        query_this_item_list_new[0].update(check_items_query)
        query_this_item_list_new[1] += ref_str_item
        query_this_item_list_new[2].extend(ref_str_item_info)

    return all_results_new


def get_reference_balanced_v2(
        use_for_check_items_origin: dict,
        use_for_check_items_opinion_similarity_dic_origin: dict,
        use_for_check_items_content_dic_origin: dict,
        query_list: list,
        min_similarity_with_query: float = 0.0001,
        top_n_indices: int = 32,
        graph=None,
        origin_query_2_ref_query: dict = None,
        mode='DocAnswerAINews'
):
    """
    按照每个查询分配满足条件的use_for_check_items，同时输出和特定查询相匹配的格式化结果。
    """

    ans = {}
    for ref_query_i in query_list:
        if graph:
            assert ref_query_i in list(origin_query_2_ref_query.values()), \
                "query_i in list(origin_query_2_ref_query.items())"
            for key, value in origin_query_2_ref_query.items():
                if value == ref_query_i:
                    origin_sub_query_i = key
            check_items_origin_query_i = graph[origin_sub_query_i]
            use_for_check_items = copy.deepcopy(check_items_origin_query_i)
            use_for_check_items_opinion_similarity_dic = dict()
            use_for_check_items_content_dic = dict()
            for chunk_id, chunk_all_info in check_items_origin_query_i.items():
                chunk_description_i = chunk_all_info['description']
                assert chunk_description_i in use_for_check_items_opinion_similarity_dic_origin.keys(), \
                    "chunk_all_info['description'] in use_for_check_items_opinion_similarity_dic_origin.keys(),"
                use_for_check_items_opinion_similarity_dic[chunk_description_i] = use_for_check_items_opinion_similarity_dic_origin[chunk_description_i]
                assert chunk_description_i in use_for_check_items_content_dic_origin.keys(), \
                    "chunk_all_info['description'] in use_for_check_items_content_dic_origin.keys(),"
                use_for_check_items_content_dic[chunk_description_i] = use_for_check_items_content_dic_origin[chunk_description_i]
        else:
            use_for_check_items, use_for_check_items_opinion_similarity_dic, use_for_check_items_content_dic = (
                copy.deepcopy(use_for_check_items_origin), copy.deepcopy(use_for_check_items_opinion_similarity_dic_origin),
                copy.deepcopy(use_for_check_items_content_dic_origin)
            )
        check_items_query, ref_str_item, ref_str_item_info = get_reference(use_for_check_items,
                                                                           use_for_check_items_opinion_similarity_dic,
                                                                           use_for_check_items_content_dic,
                                                                           [ref_query_i],
                                                                           top_n_indices=top_n_indices,
                                                                           mode=mode)

        ans[ref_query_i] = [check_items_query, ref_str_item, ref_str_item_info]
    return ans


